mapper.select:
select T_Store_Info_ID as code,T_Store_Info_Name as
name,T_Store_Info_Address as address,T_Store_Info_Manager_ID as
managerID,T_Store_Info_Class as kind,T_Store_Info_Area as
area,T_Store_Info_State as state from t_store_info

	T_Store_Info_ID=#{code}
	AND T_Store_Info_Name like #{name}
	AND T_Store_Info_Address like #{address}
	AND T_Store_Info_Manager_ID=#{managerID}
	AND T_Store_Info_Class=#{kind}
	AND T_Store_Info_Area=#{area}
	AND T_Store_Info_State=#{state}

select T_Store_Code as code,T_Store_Name as name,T_Store_ID
as
goodID,T_Store_User_ID as userID ,T_Store_Store_ID as
warehouseID,T_Store_Number as quantity,T_Store_Price as price,
T_Store_Class as classID,T_Store_Remark as remark from t_store

	T_Store_Code=#{code}
	AND T_Store_Name like #{name}
	AND T_Store_ID=#{goodID}
	AND T_Store_User_ID=#{userID}
	AND T_Store_Store_ID=#{warehouseID}
	AND T_Store_Number=#{quantity}
	AND T_Store_Price=#{price}
	AND T_Store_Class=#{classID}
	AND T_Store_Remark=#{remark}

select T_Store_Box_Code as
code,T_Store_Box_Store_ID as
warehouseID,T_Store_Box_Store_Code as
goodsID,T_Store_Box_Source as
source,T_Store_Box_Time_In as
timeIn,T_Store_Box_Admin as
adminID,T_Store_Box_Class as classID from
t_store_box

	T_Store_Box_Code=#{code}
	AND T_Store_Box_Store_ID=#{warehouseID}
	AND T_Store_Box_Store_Code=#{goodsID}
	AND T_Store_Box_Source=#{source}
	AND T_Store_Box_Time_In=#{timeIn}
	AND T_Store_Box_Admin=#{adminID}
	AND T_Store_Box_Class=#{classID}

select T_Store_State_ID as code,T_Store_State_Store_ID as
warehouseID,T_Store_State_Date as date,T_Store_State_Manager_ID as
managerID,T_Store_State_State as state,T_Store_State_Vol as
quantity,T_Store_State_Event as event,T_Store_State_Suggest as
suggest,T_Store_State_Mark as remark from t_store_state

	T_Store_State_ID=#{code}
	AND T_Store_State_Store_ID=#{warehouseID}
	AND T_Store_State_Date=#{date}
	AND T_Store_State_Manager_ID=#{managerID}
	AND T_Store_State_State=#{state}
	AND T_Store_State_Vol=#{quantity}
	AND T_Store_State_Event=#{event}
	AND T_Store_State_Suggest=#{suggest}
	AND T_Store_State_Mark=#{remark}

select T_In_Info_Main_Key as code,T_In_Info_Code as
opCode,T_In_Info_ID as
warehouseID,T_In_Info_Store_Code as
goodID,T_In_Info_Store_T_ID as
tGoodID,T_In_Info_Store_Name as
goodName,T_In_Info_Store_Price as
price,T_In_Info_Store_Class as
classID,T_In_Info_Store_Number as
quantity,T_In_Info_Remark as remark
from t_in_info

	T_In_Info_Main_Key=#{code}
	AND T_In_Info_Code=#{opCode}
	AND T_In_Info_ID=#{warehouseID}
	AND T_In_Info_Store_Code=#{goodID}
	AND T_In_Info_Store_T_ID=#{tGoodID}
	AND T_In_Info_Store_Name=#{goodName}
	AND T_In_Info_Store_Price=#{price}
	AND T_In_Info_Store_Class=#{classID}
	AND T_In_Info_Store_Number=#{quantity}
	AND T_In_Info_Remark=#{remark}

select T_In_Log_Code as code,T_In_Log_Store_Code as
goodID,T_In_Log_Store_ID as warehouseID,T_In_Log_Admin as
adminID,T_In_Log_Number as quantity,T_In_Log_Date as
date,T_In_Log_Source as source,T_In_Log_GetCode as
opcode,T_In_Log_Remark as remark from t_in_log

	T_In_Log_Code=#{code}
	AND T_In_Log_Store_Code=#{goodID}
	AND T_In_Log_Store_ID=#{warehouseID}
	AND T_In_Log_Admin=#{adminID}
	AND T_In_Log_Number=#{quantity}
	AND T_In_Log_Date=#{date}
	AND T_In_Log_Source=#{source}
	AND T_In_Log_GetCode=#{opcode}
	AND T_In_Log_Remark=#{remark}

select T_Out_main_key as code,T_Out_Code as
opCode,T_Out_Store_ID as
warehouseID,T_Out_Store_Code as
goodID,T_Out_Store_Number as
quantity,T_Out_remark as remark from t_out

	T_Out_main_key=#{code}
	AND T_Out_Code=#{opCode}
	AND T_Out_Store_ID=#{warehouseID}
	AND T_Out_Store_Code=#{goodID}
	AND T_Out_Store_Number=#{quantity}
	AND T_Out_remark=#{remark}

select T_Out_Log_Code as code,T_Out_Log_Store_Code as
goodID,T_Out_Log_Store_ID as warehouseID,T_Out_Log_Admin as
adminID,T_Out_Log_Number as quantity,T_Out_Log_Date as
date,T_Out_Log_Aim as aim,T_Out_Log_GetCode as opID,T_Out_Log_remark
as remark from t_out_log

	T_Out_Log_Code=#{code}
	AND T_Out_Log_Store_Code=#{goodID}
	AND T_Out_Log_Store_ID=#{warehouseID}
	AND T_Out_Log_Admin=#{adminID}
	AND T_Out_Log_Number=#{quantity}
	AND T_Out_Log_Date=#{date}
	AND T_Out_Log_Aim=#{aim}
	AND T_Out_Log_GetCode=#{opID}
	AND T_Out_Log_remark=#{remark}

select T_Move_Code as code,T_Move_Op_ID as
opID,T_Move_Aim_ID as
aimID,T_Move_Source_ID as sourceID,T_Move_Date as
date,T_Move_Store_Code as goodID,T_Move_Number as
quantity,T_Move_State as state from t_move

	T_Move_Code=#{code}
	AND T_Move_Op_ID=#{opID}
	AND T_Move_Aim_ID=#{aimID}
	AND T_Move_Source_ID=#{sourceID}
	AND T_Move_Date=#{date}
	AND T_Move_Store_Code=#{goodID}
	AND T_Move_Number=#{opID}
	AND T_Move_State=#{state}

