mapper.select:
select T_User_ID as id,T_User_Name as name,T_User_Password
as password,
T_User_Phone as phone,T_User_Mail as mail from t_user_one

	T_User_ID=#{id}
	T_User_Name=#{name}
	T_User_Password=#{password}
	T_User_Phone=#{phone}
	T_User_Mail=#{mail}

select T_User_ID as id,T_User_Sex as sex,T_User_Adress as
address,T_User_Kind as kind,T_User_Connect as connect,T_User_Check_ID
as checkid,T_User_Check_Work as checkwork,T_User_Mail as
checkmail,T_User_Check_Phone as checkphone,T_User_Other as other from
t_user_two

	T_User_ID = #{id}
	AND T_User_Sex = #{sex}
	T_User_Adress = #{id}
	T_User_Kind = #{kind}
	T_User_Connect = #{connect}
	T_User_Check_ID = #{checkid}
	T_User_Check_Work = #{checkwork}
	T_User_Check_Mail = #{checkmail}
	T_User_Check_Phone = #{checkphone}
	T_User_Other = #{other}

select T_Address_Index as indexA,T_Address_Content as
address,T_Address_User_ID as userId from t_address

	T_Address_Index = #{indexA}
	AND T_Address_Content =#{address}
	AND T_Address_User_ID=#{userId}
